@mixin zy-button-bgc-feedback($color, $step: 2) {
    $base-darken: 2%;

    &:hover {
        background-color: darken($color, $base-darken);
    }

    &:active {
        background-color: darken($color, $base-darken * $step);
    }
}

@mixin disabled {
    &:disabled {
        border: 1px solid #ccc !important;
        background-color: white !important;
        color: #ccc !important;
        cursor: default;

        &:hover,
        &:active {
            background-color: #fff !important;
        }
    }
}

%button-base {
    display: inline-block;
    height: 40px;
    border: none;
    line-height: 1;
    font-weight: bold;
    font-size: 1.3em;
    text-align: center;
    outline: none;
    transition: background-color 0.15s ease-in-out;
    cursor: pointer;
}

:host {
    font-size: 10px;

    .zy-button {
        @extend %button-base;

        display: flex;
        justify-content: space-around;
        align-items: center;
        box-sizing: border-box;
        padding: 0 10px;
        width: 127px;
        border-radius: 5px;
        background-color: #efeeea;
        color: #757575;

        @include zy-button-bgc-feedback(#efeeea);
        @include disabled;

        &__wrapper {
            display: flex;
            align-items: center;
        }

        // Coloring
        &.is-accent {
            background-color: #2196f3;
            color: white;

            @include zy-button-bgc-feedback(#2196f3, 3);
            @include disabled;
        }

        // Shape
        &.is-round {
            width: 40px;
            border-radius: 50%;
            font-size: 1.4em;

            @include disabled;

            ::ng-deep [icon],
            ::ng-deep zy-loader {
                & + .title {
                    margin-left: 0;
                }
            }
        }

        &.is-outlined {
            @extend .zy-button;

            border: 1px solid #9e9e9e;

            &.is-accent {
                border-color: #2196f3;
                background-color: #bbdefb;
                color: #2196f3;

                @include zy-button-bgc-feedback(#bbdefb, 3);
                @include disabled;
            }
        }

        &.is-ghost {
            @extend .zy-button;

            background-color: transparent;

            .title,
            ::ng-deep [icon] {
                transition: color .15s ease-in-out;
            }

            &.is-accent {
                background-color: transparent;
                color: #2196f3;

                &:hover {
                    .title,
                    ::ng-deep [icon] {
                        color: darken(#2196f3, 10%);
                    }
                }
            }

            &:hover {
                .title,
                ::ng-deep [icon] {
                    color: darken(#757575, 10%);
                }
            }
        }

        ::ng-deep [icon],
        ::ng-deep zy-loader {
            & + .title {
                margin-left: 10px;
            }
        }
    }

    .zy-split-button {
        position: relative;
        z-index: 20;
        display: inline-block;
        color: #757575;

        &__wrapper {
            position: relative;
            z-index: 30;
            display: flex;
            align-items: center;
        }

        &__left-part {
            @extend %button-base;

            padding: 0 10px;
            width: 127px;
            border-radius: 5px 0 0 5px;
            background-color: #efeeea;
            color: inherit;
            font-size: 1.3em;

            @include zy-button-bgc-feedback(#efeeea);
            @include disabled;
        }

        &__right-part {
            @extend .zy-split-button__left-part;

            width: 40px;
            border-radius: 0 5px 5px 0;
            border-left: 1px solid white;
            font-size: 1.8em;
        }

        &.is-accent {
            color: white;

            .zy-split-button__left-part {
                background-color: #2196f3;
                @include zy-button-bgc-feedback(#2196f3, 3);
            }

            .zy-split-button__right-part {
                background-color: #2196f3;
                @include zy-button-bgc-feedback(#2196f3, 3);
            }
        }

        .sub-menu {
            position: absolute;
            top: 100%;
            z-index: 10;
            box-sizing: border-box;
            margin-top: -10px;
            width: 100%;
            color: black;
            border: 1px solid #ccc;
            border-radius: 3px;
            font-size: 1.3em;
            line-height: 1;
            transform: translateY(-7px);
            opacity: 0.45;
            transition: all 0.15s ease-in-out;

            &.is-opening {
                transform: translateY(7px);
                opacity: 1;
            }

            &__item {
                background-color: #fff;
                border-bottom: 1px solid #ccc;

                &:last-child {
                    border-bottom: none;
                }
            }

            .item {
                &__button {
                    display: block;
                    padding: 10px 5px;
                    margin: 0;
                    width: 100%;
                    background: none;
                    background-color: #fff;
                    border: none;
                    transition: background-color 0.15s ease-in-out;
                    outline: none;
                    cursor: pointer;

                    &:hover {
                        background-color: rgba(#ccc, 0.56);
                    }

                    &:active {
                        background-color: darken(#ccc, 0.5%);
                    }

                    &[disabled] {
                        cursor: default;

                        &:hover {
                            background-color: #fff;
                        }
                    }
                }
            }
        }
    }
}

